x 'cd "C:\your\local\directory"';
proc import 
  datafile='yourfile.csv' 
  out=yourdataset 
  dbms=csv 
  replace;
  getnames=yes;
run;
/* 按区域总结数据 */
proc summary data=yourdataset nway;
  class area;
  var count;
  output out=summary_dataset sum=;
run;
